BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "alltasks" (
	"id"	INTEGER NOT NULL UNIQUE,
	"task"	TEXT NOT NULL,
	"level"	NUMERIC,
	"img"	INTEGER,
	"active"	NUMERIC,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "pinkgame" (
	"id"	INTEGER NOT NULL,
	"task"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "redgame" (
	"id"	INTEGER NOT NULL,
	"task"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "whitegame" (
	"id"	INTEGER NOT NULL,
	"task"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "yellowgame" (
	"id"	INTEGER NOT NULL,
	"task"	TEXT,
	PRIMARY KEY("id")
);
CREATE VIEW "randall" AS SELECT * 
FROM alltasks
LIMIT 1 
OFFSET ABS(RANDOM()) % MAX((SELECT COUNT(*) FROM alltasks), 1);
CREATE VIEW "randred" AS SELECT *
FROM redgame
LIMIT 1
OFFSET ABS(RANDOM()) % MAX((SELECT COUNT(*) FROM redgame), 1);
CREATE VIEW "randwhite" AS SELECT * 
FROM whitegame
LIMIT 1 
OFFSET ABS(RANDOM()) % MAX((SELECT COUNT(*) FROM whitegame), 1);
CREATE VIEW "randyellow" AS SELECT * 
FROM yellowgame
LIMIT 1 
OFFSET ABS(RANDOM()) % MAX((SELECT COUNT(*) FROM yellowgame), 1);
CREATE VIEW "randpink" AS SELECT * 
FROM pinkgame
LIMIT 1 
OFFSET ABS(RANDOM()) % MAX((SELECT COUNT(*) FROM pinkgame), 1);
COMMIT;
